            'content'       => "<!-- wp:cover {\"overlayColor\":\"white\",\"isDark\":false,\"align\":\"full\",\"className\":\"f-section\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\"}}}} -->\n<div class=\"wp-block-cover alignfull is-light f-section\" style=\"padding-top:var(--wp--preset--spacing--70);padding-bottom:var(--wp--preset--spacing--70)\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-white-background-color has-background-dim-100 has-background-dim\"></span><div class=\"wp-block-cover__inner-container\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"bottom\":\"var:preset|spacing|60\",\"right\":\"0\",\"top\":\"var:preset|spacing|40\"}}},\"layout\":{\"type\":\"constrained\",\"contentSize\":\"980px\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--40);padding-right:0;padding-bottom:var(--wp--preset--spacing--60)\"><!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"40px\"}}} -->\n<h2 class=\"has-text-align-center\" style=\"font-size:40px\">Lorem ipsum</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"24px\"}}} -->\n<p class=\"has-text-align-center\" style=\"font-size:24px\">Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Aliquam erat volutpat. Nullam sagittis. Praesent ac massa at ligula </p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:columns {\"verticalAlignment\":null,\"align\":\"wide\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"0\",\"bottom\":\"var:preset|spacing|80\"}}}} -->\n<div class=\"wp-block-columns alignwide\" style=\"padding-top:0;padding-bottom:var(--wp--preset--spacing--80)\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{