            'content'       => "<!-- wp:cover {\"dimRatio\":50,\"customOverlayColor\":\"#fafaff\",\"isDark\":false,\"align\":\"full\",\"className\":\"f-section\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|80\",\"bottom\":\"var:preset|spacing|80\"}}}} -->\n<div class=\"wp-block-cover alignfull is-light f-section\" style=\"padding-top:var(--wp--preset--spacing--80);padding-bottom:var(--wp--preset--spacing--80)\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-background-dim\" style=\"background-color:#fafaff\"></span><div class=\"wp-block-cover__inner-container\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"bottom\":\"var:preset|spacing|60\",\"right\":\"0\",\"top\":\"var:preset|spacing|40\"}}},\"layout\":{\"type\":\"constrained\",\"contentSize\":\"1020px\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--40);padding-right:0;padding-bottom:var(--wp--preset--spacing--60)\"><!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"40px\"}}} -->\n<h2 class=\"has-text-align-center\" style=\"font-size:40px\">Praesent ac sem?</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"24px\"}}} -->\n<p class=\"has-text-align-center\" style=\"font-size:24px\">Nulla Lorem mollit cupidatat irure. Laborum magna nulla duis ullamco cillum dolor. Voluptate exercitation incididunt aliquip deserunt reprehenderit elit laborum.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"layout\":{\"type\":\"constrained\",\"contentSize\":\"1120px\"}} -->\n<div class=\"wp-block-group\"><!-- wp:columns {\"verticalAlignment\":null} -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"verticalAlignment\":\"top\"} -->\n<div class=\"wp-block-column is-vertically-aligned-top\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|50\",\"right\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|50\"},\"blockGap\":\"var:preset|spacing|40\"},\"border\":{\"radius\":\"8px\"}},\"backgroundColor\":\"white\",\"className\":\"f-block f-block-shadow\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group f-block f-block-shadow has-white-background-color has-background\" style=\"border-radius:8px;padding-top:var(--wp--preset--spacing--50);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50);padding-left:var(--wp--preset--spacing--50)\"><!-- wp:image {\"sizeSlug\":\"large\",\"style\":{},\"className\":\"f-block-icon\"} -->\n<figure class=\"wp-block-image size-large f-block-icon\"><img src=\"" . get_stylesheet_directory_uri() . "/dist/images/icon-contact.svg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"fontSize\":\"normal\"} -->\n<p class=\"has-normal-font-size\">Aenean tellus metus, bibendum sed, posuere ac, mattis non</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"backgroundColor\":\"white\",\"textColor\":\"dark\",\"style\":{\"typography\":{\"fontSize\":\"20px\",\"fontStyle\":\"normal\",\"fontWeight\":\"700\"},\"spacing\":{\"padding\":{\"top\":\"0\",\"right\":\"0\",\"bottom\":\"0\",\"left\":\"0\"}},\"border\":{\"radius\":\"3px\"}},\"className\":\"f-btn-link is-style-fill\"} -->\n<div class=\"wp-block-button has-custom-font-size f-btn-link is-style-fill\" style=\"font-size:20px;font-style:normal;font-weight:700\"><a class=\"wp-block-button__link has-dark-color has-white-background-color has-text-color has-background wp-element-button\" href=\"#\" style=\"border-radius:3px;padding-top:0;padding-right:0;padding-bottom:0;padding-left:0\">Fusce</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|50\",\"right\":