lumn -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|50\",\"right\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|50\"},\"blockGap\":\"var:preset|spacing|40\"},\"border\":{\"radius\":\"8px\"}},\"backgroundColor\":\"white\",\"className\":\"f-block f-block-shadow\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group f-block f-block-shadow has-white-background-color has-background\" style=\"border-radius:8px;padding-top:var(--wp--preset--spacing--50);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50);padding-left:var(--wp--preset--spacing--50)\"><!-- wp:image {\"sizeSlug\":\"large\",\"className\":\"f-block-icon\"} -->\n<figure class=\"wp-block-image size-large f-block-icon\"><img src=\"" . get_stylesheet_directory_uri() . "/dist/images/icon-learning.svg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"fontSize\":\"normal\"} -->\n<p class=\"has-normal-font-size\">Etiam iaculis nunc ac metus. Curabitur blandit mollis lacus?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"backgroundColor\":\"white\",\"textColor\":\"dark\",\"style\":{\"typography\":{\"fontSize\":\"20px\",\"fontStyle\":\"normal\",\"fontWeight\":\"700\"},\"spacing\":{\"padding\":{\"top\":\"0\",\"right\":\"0\",\"bottom\":\"0\",\"left\":\"0\"}},\"border\":{\"radius\":\"3px\"}},\"className\":\"f-btn-link is-style-fill\"} -->\n<div class=\"wp-block-button has-custom-font-size f-btn-link is-style-fill\" style=\"font-size:20px;font-style:normal;font-weight:700\"><a class=\"wp-block-button__link has-dark-color has-white-background-color has-text-color has-background wp-element-button\" href=\"#\" style=\"border-radius:3px;padding-top:0;padding-right:0;padding-bottom:0;padding-left:0\">Etiam</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:cover -->",
        )
    );
   
